import { Component, OnInit } from '@angular/core';
import { loginData } from '../loginData';
import { LoginService } from '../login.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  logindata: loginData = new loginData();

  constructor(private loginService: LoginService , private router: Router) {}

  ngOnInit(): void {} 

  onSubmit(){
    console.log(this.logindata);
    this.login();
  }
  login() {
   this.loginService.loginUser(this.logindata).subscribe((data: any) => {
    console.log(data);
    localStorage.setItem("token", data.authenticationToken);
    localStorage.setItem("username",data.username);
    alert("Login Successful");
    this.router.navigate(['/posts']);
   },
   error=> console.log(error)
    );
  }

  getLoggedInUser(){
    return localStorage.getItem("username");
  }

}
<div class="container justify-content-center">
    <form class="form-login" #loginForm="ngForm" (ngSubmit)="onSubmit()">
            <h1 class="h3 mb-3 font-weight-normal"> Please Login</h1>
            <div class="form-group">
                <label >Username</label>
                <input type="text" id="username" name="username" #username="ngModel" [(ngModel)]="logindata.username" class="form-control" placeholder="Username" required="" autofocus="">
            </div>
            <div class="form-group">
                <label >Password</label>
                <input type="password" id="password" name="password" #password="ngModel" [(ngModel)]="logindata.password" class="form-control" placeholder="Password" required="" autofocus="">
            </div>
            <button type="submit" class="btn btn-primary btn-lg">Login</button>
    </form>
</div>
