import { Component, OnInit } from '@angular/core';
import { PostcommentService } from '../postcomment.service';
import { Comment } from '../Comment';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-postcomment',
  templateUrl: './postcomment.component.html',
  styleUrls: ['./postcomment.component.css']
})
export class PostcommentComponent implements OnInit{
pid !:number
constructor(private postCommentService: PostcommentService, private route:ActivatedRoute){}

ngOnInit(): void {
  this.pid=this.route.snapshot.params[`id`]
}
  commentDao: Comment = new Comment();

    onSubmit(){
    this.postComment();
  }
  postComment() {
    this.postCommentService.postComment(this.commentDao, this.pid).subscribe((data) =>{
      console.log(data);
      alert('comments added')
    },
    error => console.log(error)
    );
  }
}

<div class="container justify-content-center">
    <form class="form-pc" #pcForm="ngForm" (ngSubmit)="onSubmit()">
            <div class="form-group">
                <label>comment</label>
                <input type="text" id="comment" name="comment" #comment="ngModel" [(ngModel)]="commentDao.comment" class="form-control" placeholder="comment" required="" autofocus="">
            </div>
            <button type="submit" class="btn btn-primary btn-lg">Comment</button>
    </form>
</div>
