import { Component, OnInit } from '@angular/core';
import { ViewmyblogsService } from '../viewmyblogs.service';
import { Post } from '../Post';

@Component({
  selector: 'app-viewmyblogs',
  templateUrl: './viewmyblogs.component.html',
  styleUrls: ['./viewmyblogs.component.css']
})
export class ViewmyblogsComponent implements OnInit{

  myPosts: Post[] | undefined;
  uid=0;
  constructor(private viewMyBlogsService: ViewmyblogsService){}
  ngOnInit(): void {
   this.MyBlogs();
  //  this.MyBlogsResponse();
  }
  MyBlogs() {
    this.viewMyBlogsService.getUserId().subscribe(data=>{
      this.uid=data;
      console.log("user id in myblogs",this.uid)
    }),this.MyBlogsResponse()
  }

  MyBlogsResponse(){
    console.log("p")
    this.viewMyBlogsService.getMyBlogs(this.uid).subscribe((data)=>{
      this.myPosts=data
    })
  }
}
<h3 class="text-center">My Blog Posts</h3>

<table class="table table-striped text-center" >
    <thead>
        <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Content</th>
        </tr>

    </thead>
    <tbody>
        <tr *ngFor ="let post of myPosts">
            <td>{{post.id}}</td>
            <td>{{post.title}}</td>
            <td>{{post.content}}</td>
        </tr>
    </tbody>
</table>
