import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Post } from './Post';

@Injectable({
  providedIn: 'root'
})
export class ViewmyblogsService  {
  private baseURL="http://localhost:8008";

  constructor(private httpClient: HttpClient) { }

 async getUserId():Promise<Observable<any>>{
   let headers= new HttpHeaders().set("Authorization",`Bearer ${localStorage.getItem(`token`)}`)
    return this.httpClient.get(`${this.baseURL}/getuserid`,{headers})
    }

  async getMyBlogs(uid:any):Promise<Observable<Post[]>>{
    console.log("id at 20",uid)
    ;(await this.getUserId()).subscribe((data)=>{
      uid=data
      console.log("id at 23",uid)

    })
    let headers= new HttpHeaders().set("Authorization",`Bearer ${localStorage.getItem(`token`)}`)
    console.log("id at 27",uid)
   return this.httpClient.get<Post[]>(`${this.baseURL}/myposts/${uid}`, {headers});  

  }


}
