import { Component, OnInit } from '@angular/core';
import { PostcommentService } from '../postcomment.service';

@Component({
  selector: 'app-postcomment',
  templateUrl: './postcomment.component.html',
  styleUrls: ['./postcomment.component.css']
})
export class PostcommentComponent implements OnInit {
  commentDao: Comment = new Comment();

  ngOnInit(): void {}
constructor(private postCommentService: PostcommentService){}

  onSubmit(){
    this.postComment();
  }
  postComment() {
    this.postCommentService.postComment(this.commentDao).subscribe((data) =>{
      console.log(data);
    },
    error => console.log(error)
    );
  }

}
