import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Post } from './Post';
import { LoginComponent } from './login/login.component';

@Injectable({
  providedIn: 'root'
})
export class ViewmyblogsService  {
  private baseURL="http://localhost:8008";

  constructor(private httpClient: HttpClient, private loginComponent: LoginComponent) { }


  getUserId(){
     let uid
   let headers= new HttpHeaders().set("Authorization",`Bearer ${localStorage.getItem(`token`)}`)
     this.httpClient.get(`${this.baseURL}/getuserid`,{headers}).subscribe((data: any)=> {
      uid=data;
    }
    );
    return uid;
  }

  viewMyBlogs():Observable<Post[]> {
    let username  = this.loginComponent.getLoggedInUser();
     let uid= this.getUserId();
    let headers= new HttpHeaders().set("Authorization",`Bearer ${localStorage.getItem(`token`)}`)
   return this.httpClient.get<Post[]>(`${this.baseURL}/myposts/${uid}`, {headers});
  }
}
