import { Component, OnInit } from '@angular/core';
import { PostcommentService } from '../postcomment.service';

@Component({
  selector: 'app-postcomment',
  templateUrl: './postcomment.component.html',
  styleUrls: ['./postcomment.component.css']
})
export class PostcommentComponent {
  commentDao :Comment = new Comment();

constructor(private postCommentService: PostcommentService){}

  onSubmit(){
    this.postComment();
  }
  postComment() {
    this.postCommentService.postComment(this.commentDao).subscribe((data) =>{
      console.log(data);
    },
    error => console.log(error)
    );
  }

}

<div class="container justify-content-center">
    <form class="form-pc" #pcForm="ngForm" (ngSubmit)="onSubmit()">
            <h1 class="h3 mb-3 font-weight-normal"> Please Login</h1>
            <div class="form-group">
                <label>comment</label>
                <input type="text" id="comment" name="comment" #comment="ngModel" [(ngModel)]="commentDao.comment" class="form-control" placeholder="comment" required="" autofocus="">
            </div>
            <button type="submit" class="btn btn-primary btn-lg">Comment</button>
    </form>
</div>
