import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Post } from './Post';
import { LoginComponent } from './login/login.component';

@Injectable({
  providedIn: 'root'
})
export class ViewmyblogsService  {
  private baseURL="http://localhost:8008";
myblogs:any
  constructor(private httpClient: HttpClient, private loginComponent: LoginComponent) { }

  getUserId():Observable<any>{

   let headers= new HttpHeaders().set("Authorization",`Bearer ${localStorage.getItem(`token`)}`)
    return this.httpClient.get(`${this.baseURL}/getuserid`,{headers})
  }

  viewMyBlogs():Observable<Post[]> {
    let username  = this.loginComponent.getLoggedInUser();
      this.getUserId().subscribe((uid)=> {
        if(uid!=null){
          let headers= new HttpHeaders().set("Authorization",`Bearer ${localStorage.getItem(`token`)}`)
           this.myblogs= this.httpClient.get<Post[]>(`${this.baseURL}/myposts/${uid}`, {headers});
        }
      })
      return this.myblogs;


  }
}
