import { Component, OnInit } from '@angular/core';
import { ViewCommentService } from '../view-comment.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-view-comment',
  templateUrl: './view-comment.component.html',
  styleUrls: ['./view-comment.component.css']
})
export class ViewCommentComponent implements OnInit{
  pid !:number
  constructor(private viewCommentService: ViewCommentService, private route:ActivatedRoute){}
  ngOnInit(): void {
   this.pid=this.route.snapshot.params[`id`];
  }




  viewcomments(pid:number){

  }

}
<h3 class="text-center">Comments</h3>
<table class="table table-striped text-center" >
    <thead>
        <tr>
            <th>Id</th>
            <th>Comment</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor ="let comment of comments">
            <td>{{comment.id}}</td>
            <td>{{post.comment}}</td>
            <td><button (click)="addcomment(post.id)" class="btn btn-primary"> add comment</button> </td>
            <td><button (click)="viewcomments(post.id)" class="btn btn-primary"> view comments</button> </td>
        </tr>
    </tbody>
</table>
