import { Component, OnInit } from '@angular/core';
import { Post } from '../Post';
import { CreateBlogService } from '../create-blog.service';
import { Router } from '@angular/router';
import { LoginService } from '../login.service';

@Component({
  selector: 'app-create-blog',
  templateUrl: './create-blog.component.html',
  styleUrls: ['./create-blog.component.css']
})
export class CreateBlogComponent  implements OnInit{

  post: Post = new Post();

  constructor(private createBlogService: CreateBlogService, private router: Router, private loginService: LoginService) {}
  ngOnInit(): void {}

  onSubmit(){
    this.createblog();
  }
  createblog() {
    if(this.loginService.isAuthenticated()) {
      this.createBlogService.createblog(this.post).subscribe((data:any)=>{
        console.log(data)
        alert('Posted')
        localStorage.setItem("pid",data.id);
        this.router.navigate([`/viewmyblogs`])
       },
       error => console.log(error)
       );
    }
    else {
      alert("Please Login to Continue")
      this.router.navigate([`/login`])
    }

  }

}
<div class="createblog">
    <div class="container">
        <form class="blogpost" #createblog="ngForm" (ngSubmit)="onSubmit()">
            <h1 class="h3 mb-3 font-weight-normal"> Create Your Blog</h1>
            <div class="form-group">
                <label >Title</label>
                <input type="text" id="title" name="title" #title="ngModel" [(ngModel)]="post.title" class="form-control" placeholder="Title" required="" autofocus="">
            </div>
            <div class="form-group">
                <label >Content</label>
                <input type="text" id="content" name="content" #content="ngModel" [(ngModel)]="post.content" class="form-control" placeholder="Password" required="" autofocus="">
            </div>
            <button type="submit" class="btn btn-primary btn-lg">Post</button>

        </form>

    </div>
</div>
