import { Component, OnInit } from '@angular/core';
import { User } from '../User';
import { RegisterService } from '../register.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  user: User= new User();
  constructor(private registerService: RegisterService, private httpClient: HttpClient) {}

  ngOnInit(): void {}

  onSubmit(){
    this.register();
  }
  register() {
    this.registerService.registerUser(this.user).subscribe( data => {
      console.log(data);
      alert("Registration Successful");
    },
    error => console.log(error)
    );
  }

}
<div class="container justify-content-center ">
    <form #myForm="ngForm" class="form-signup"  (ngsubmit)="onSubmit()">
        <h1 class="h3 mb-3 font-weight-normal"> Please Register</h1>
        <div class="form-group">
            <label >Username</label>
            <input type="text" id="userName" class="form-control" name="userName" #userName="ngModel" ([ngModel])="user.userName" placeholder="Username" required="" autofocus="">
        </div>
        <div class="form-group"> 
            <label >EmailId</label>
            <input type="text" id="emailId" name="emailId" #emailId="ngModel" class="form-control" placeholder="EmailId" required="" autofocus="">
        </div>
        <div class="form-group">
            <label >Password</label>
            <input type="password" id="password" name="=password" #password="ngModel" class="form-control" placeholder="Password" required="" autofocus="">
        </div>
        <div class="form-group">
            <label >Password</label>
            <input type="password" id="password" class="form-control" placeholder="Confirm Password" required="" autofocus="">
        </div>

        <button type="button" class="btn btn-primary btn-lg">Submit</button>

    </form>
</div>
