import { Component, OnInit } from '@angular/core';
import { User } from '../User';
import { RegisterService } from '../register.service';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  user: User = new User();
  constructor(private registerService: RegisterService, private router: Router) {}

  ngOnInit(): void {}

  onSubmit(){
    this.register();
  }
  register() {
    this.registerService.registerUser(this.user).subscribe( data => {
      console.log(data);
      alert("Registration Successful");
      this.router.navigate(['/login']);
    },
    error => console.log(error)
    );
  }

}
<div class="container justify-content-center ">
    <form #userForm="ngForm" class="form-signup"  (ngSubmit)="onSubmit()">
        <h1 class="h3 mb-3 font-weight-normal"> Please Register</h1>
        <div class="form-group">
            <label >Username</label>
            <input type="text"  class="form-control" id="userName" name="userName" #userName="ngModel" [(ngModel)]="user.userName" placeholder="Username" required="" autofocus="">
        </div>
        <div class="form-group"> 
            <label >EmailId</label>
            <input type="text"  id="emailId" name="emailId" #emailId="ngModel" [(ngModel)]="user.emailId" class="form-control" placeholder="EmailId" required="" autofocus="">
        </div>
        <div class="form-group">
            <label >Password</label>
            <input type="password"  id="password" name="=password" #password="ngModel" [(ngModel)]="user.password" class="form-control" placeholder="Password" required="" autofocus="">
        </div>
        <button type="submit" class="btn btn-primary btn-lg">Submit</button>
        <div>
            already a User! click <a href="">Here</a> to Login
        </div>
    </form>
</div>
