import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Post } from '../Post';
import { ViewblogsService } from '../viewblogs.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-viewblogs',
  templateUrl: './viewblogs.component.html',
  styleUrls: ['./viewblogs.component.css']
})
export class ViewblogsComponent implements OnInit{
  posts: Post[] | undefined;

  constructor(private viewBlogService: ViewblogsService, private router: Router) {}
  ngOnInit(): void {
    this.viewPosts();
  }
  viewPosts() {
   this.viewBlogService.viewAllBlogs().subscribe((data)=> {
    this.posts=data;
   })
  }

  addcomment(id: number){
    this.router.navigate([`postcomment`, id]);
  }

}
<div class="header">
    <div class="container">
        <div class="row">
            <div class="col-md-6">
                <h1 >Blog Application</h1>

            </div>
            <div class="col-md-6">
                <a  routerLink="/createblog" class=" login btn btn-dark"> CreateBlog </a>
                <a routerLink="/viewmyblogs" class=" register btn btn-dark"> ViewMyBlogs </a> 
            </div> 
        </div>

    </div>

</div>
<h3 class="text-center">Blog Posts</h3>
<table class="table table-striped text-center" >
    <thead>
        <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Content</th>
        </tr>

    </thead>
    <tbody>
        <tr *ngFor ="let post of posts">
            <td>{{post.id}}</td>
            <td>{{post.title}}</td>
            <td>{{post.content}}</td>
            <td><button (click)="addcomment(post.id)" class="btn btn-primary"> add comment</button> </td>
            <td><button routerLink="/postcomment" class="btn btn-primary"> view comments</button> </td>
        </tr>
    </tbody>
</table>
